import React from 'react'
import shoppingcart from '../assets/shoppingcart.png'
import simpleCalc from '../assets/simpleCalc.png'
import muiNavBar from '../assets/muiNavBar.png'
const PortFol = () => {

  const portfols = [
    {
      id: 1,
      src: shoppingcart
    },
    {
      id: 2,
      src: simpleCalc
    },
    {
      id: 3,
      src: muiNavBar
    }
  ]
  return (
    <div name="portfolio" className='bg-gradient-to-b from-black to-gray-800 w-full text-white md:h-screen'>

      <div className='max-w-screen-lg p-4 mx-auto flex flex-col justify-center w-full h-full'>
        <div className='pb-8'>
          <p className='text-4xl font-bold inline border-b-4 border-gray-500'>PortFolio</p>
          <p className='py-6 text-xl'> check out my work here </p>
        </div>
        <div className='grid sm:grid-cols-2 md:grid-cols-3 gap-8 px-12 sm:px-0'>
          {
            portfols.map(({ id, src }) => (
              <div key={id} className='shadow-md shadow-gray-600 rounded-lg'>
                <img src={src} alt='' className='rounded-md duration-200 hover:scale-105' />
                <div className='flex items-center justify-center'>
                  <button className='w-1/2 px-6 py-3 m-4 duation-200 hover:scale-105'>Demo</button>
                  <button className='w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105'>Code</button>
                </div>
              </div>
            ))
          }
        </div>


      </div>
    </div>
  )
}

export default PortFol
