import React from 'react'
import { FaGithub, FaLinkedinIn } from "react-icons/fa";
import { HiOutlineMail } from "react-icons/hi";
import { BsFillPersonLinesFill } from "react-icons/bs";

const SocLinks = () => {

  const links = [
    {
      ids: 1,
      child: (
        <>
          LinkedIn <FaLinkedinIn size={30} />
        </>
      ),
      href: "https://linkedin.com",
      style: "rounded-tr-md"
    },
    {
      ids: 2,
      child: (
        <>
          GitHub <FaGithub size={30} />
        </>
      ),
      href: "https://github.com/"
    },
    {
      ids: 3,
      child: (
        <>
          Mail <HiOutlineMail size={30} />
        </>
      ),
      href: "mailto:https://mail.google.com/"
    },
    {
      ids: 4,
      child: (
        <>
          Resume <BsFillPersonLinesFill size={30} />
        </>
      ),
      href: "/reume,pdf",
      style: "rounded-br-md",
      download: true,
    },
  ];

  return (
    <div className='flex flex-col top-[35%] left-0 fixed'>
      <ul>

        {links.map(({ids, child, href, style, download}) => (
          <li key={ids} className= {
            'flex justify-between item-center w-40 h-14 px-4 ml-[-100px] hover:ml-[-10px] hover:rounded-md duration-300 bg-gray-500' + '' + style} >
            <a href={href}
              className='flex justify-between items-center w-full text-white'
              download={download} target='_blank'>
              {child}
            </a>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default SocLinks
